package REply;

public class Patternkmlbey {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=0;i<5;i++) {
			for(int y=0;y<i;y++) {
				System.out.print("*"+" ");
			}System.out.println();
		}
		for(int i=1;i<9;i++) {
			for(int y=1;y<i;y++) {
				System.out.print(y+" ");
			}System.out.println();

	}
		for(int i=0;i>=10;i++) {
			for(int y=i;y>=0;y--) {
				System.out.print(y+" ");
			}System.out.println();

	}
		for(int i=0;i>=10;i++) {
			for(int y=i;y>=0;y--) {
				System.out.print("*"+" ");
			}System.out.println();

		}
}
}