package com.syntax.class07;

public class Whiledemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int count=1;
		 String greeting="hello";
		 while(true) {//this is be forever true
			 count++;
			 System.out.println(greeting);
			 if(count==6) {
				 break;
			 }
		 }

	}

}
